mapper.update:
update sensorMapping
set sensorModel = #{sensorModel,jdbcType=VARCHAR},
  createTime = #{createTime,jdbcType=TIMESTAMP},
  updateTime = #{updateTime,jdbcType=TIMESTAMP},
  sensorType = #{sensorType,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

update sensorMapping
set updateTime = DEFAULT,
  sensorType = #{sensorType,jdbcType=VARCHAR}
where sensorModel = #{sensorModel,jdbcType=VARCHAR}

mapper.select:
select id, sensorModel, createTime, updateTime, sensorType
from sensorMapping
where id = #{id,jdbcType=BIGINT}
